	/**
	 * @see HttpServlet#doGet(HttpServletRequest request, HttpServletResponse response)
	 */
	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		response.setContentType("application/json;charset=UTF-8");
		response.setHeader("Access-Control-Allow-Origin", "*");
		PrintWriter out=response.getWriter();
		
		String piclist=request.getParameter("piclist");
		if(piclist!=null&&!piclist.isEmpty()){
			respondList(request,response,out);
		}else{
			respondSingle(request,response,out);
		}
		
	}

	private void respondSingle(HttpServletRequest request, HttpServletResponse response,PrintWriter out) {
		String userName=request.getParameter("name");
		String imgId=request.getParameter("imgid");
		
		String filePath="hello.txt";
		if(userName!=null&&!userName.isEmpty()&&imgId!=null&&!imgId.isEmpty()) {
			filePath=imgId+".txt";//"hello.txt";
		}
		//String dir="/upload";//一定要有“/”
		String realPath=getServletContext().getRealPath(dir);

		String realFilePath=realPath+File.separatorChar+filePath;
		System.out.println(realFilePath);
		String picData=null;
		String picUrl=dir+"/"+filePath;
		List<TuyaAction> actions=null;
		try{
			picData=FileUtil.readFromFile(realFilePath);
			actions=JSON.parseArray(picData, TuyaAction.class);
		}catch(FileNotFoundException e){
			picUrl="error:发生错误：<br>该作品可能已被删除";
		}catch(JSONException e){
			picUrl="error:发生错误：<br>该作品保存的数据格式有问题，无法回放";
		}catch(IOException e){
			picUrl="error:发生错误：<br>读取数据时出错了";
		}
		TuyaPic pic=new TuyaPic();
		pic.setAuthor("xiaomao");
		pic.setUrl(picUrl);
		pic.setCreated(new Date());
		pic.setActions(actions);
		
		out.print(JSON.toJSONString(pic));
		
	}

	private void respondList(HttpServletRequest request, HttpServletResponse response,PrintWriter out) {
		
		Map<String,Object>result=new HashMap<String, Object>();;
		
		//String dir="/upload";//一定要有“/”
		String realPath=getServletContext().getRealPath(dir);
		File uploadDir=new File(realPath);
		File[] files=uploadDir.listFiles(new FilenameFilter(){
			@Override
			public boolean accept(File dir, String name) {
				return name.endsWith(".png");
			}});
		TreeMap<Long,String> tm = new TreeMap<Long,String>();
		for(File f:files){
			tm.put(f.lastModified(), f.getName());
		}
		Collection<String> pics=tm.descendingMap().values();
/*		String[] pics=uploadDir.list(new FilenameFilter(){
			@Override
			public boolean accept(File dir, String name) {
				return name.endsWith(".png");
			}});*/
		result.put("status", "ok");
		result.put("piclist", pics);
		out.print(JSON.toJSONString(result));
	}

